import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import supabase from '../supabase';

import {
  Flex,
  Heading,
  Input,
  Button,
  FormControl,
  FormLabel,
  Switch,
  useColorMode,
  useColorModeValue,
  useToast
} from '@chakra-ui/react';

import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL;
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY;

createClient(supabaseUrl, supabaseAnonKey);

const Login = () => {
  const { toggleColorMode } = useColorMode();
  const toast = useToast();
  const navigate = useNavigate();
  const formBackground = useColorModeValue('gray.100', 'gray.700');
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  async function SignIn() {
    const signUp = await supabase.auth.signIn({

      email: email,
      password: password,
    })
    console.log("data i i",signUp)

    toast({
      title: !signUp.user? 'Invalid creadentials' : 'Sign In Successfull!',
      position: 'top',
      status: signUp.error ? 'error' : 'success',
      duration: 2000,
      isClosable: true,
    });
if(signUp.user){
  console.log("data i i",signUp)

  navigate('/todo');

}



  }

  return (
    <Flex h="100vh" alignItems="center" justifyContent="center">
      <Flex
        flexDirection="column"
        bg={formBackground}
        p={12}
        borderRadius={8}
        boxShadow="lg"
        textAlign={center}
      >
        <Heading mb={6} fontFamily="Arial">Log In</Heading>
        <Input
          placeholder="johndoe@gmail.com"
          type="email"
          variant="filled"
          onChange={(e) => setEmail(e.target.value)}
          mb={3}
        />
        <Input
          placeholder="**********"
          type="password"
          variant="filled"
          onChange={(e) => setPassword(e.target.value)}
          mb={6}
        />
        <Button colorScheme="blue" onClick={SignIn} mb={4}>
          Log In
        </Button>
        <a href="/signup">
        Don't have an account?
          <Button colorScheme="blue" variant="link">
             Create an account
          </Button>
        </a>
        {/* <FormControl display="flex" alignItems="center">
          <FormLabel htmlFor="dark_mode" mb="0">
            Enable Dark Mode?
          </FormLabel>
          <Switch
            id="dark_mode"
            colorScheme="teal"
            size="lg"
            onChange={toggleColorMode}
          />
        </FormControl> */}
      </Flex>
    </Flex>
  );
};

export default Login;
